mapper.update:
UPDATE group_sensor
	SET s_display = #{value}
	WHERE gs_no = (SELECT l.gs_no FROM limit_Table l WHERE l_no=#{l_no})

mapper.select:
SELECT j_no, g_no,	j_name,	j_text,	j_target,	j_class
	FROM job
	WHERE j_no = #{j_no}

mapper.update:
UPDATE job SET
	j_name	=#{j_name	},
	j_text	=#{j_text},
	j_target	=#{j_targetInput},
	j_class	=#{j_class}
WHERE j_no = #{j_no}

mapper.select:
SELECT j_nos FROM event_job
	WHERE	no = #{no}
	AND		e_no = #{e_no}
